<?php  class do4me_login_check{    public $conn;    public $remember;    public $cookie_passwords;    /* check the admin */    public function checkadmin($name,$password){       $query = "select * from `d4madmin_info` where `email` = '".$name."' and `password` = '".$password."' and `enable_booking` = 'Y' ";        $result=mysqli_query($this->conn,$query);        $value=mysqli_fetch_assoc($result);        if(isset($value['id']) && $value['id']!=0){            if($value['role']=="admin"){                $_SESSION['d4madminid'] = $value['id'];                $_SESSION['d4museremail'] = $value['email'];            }else{                $_SESSION['d4mstaffid'] = $value['id'];                $_SESSION['d4museremail'] = $value['email'];                            }                            if($this->remember == "true"){                    setcookie('do4me_username',$name, time() + (86400 * 30), "/");                    setcookie('do4me_password',$this->cookie_passwords, time() + (86400 * 30), "/");                    setcookie('do4me_remember',"checked", time() + (86400 * 30), "/");                }                else{                    unset($_COOKIE['do4me_username']);                    unset($_COOKIE['do4me_password']);                    unset($_COOKIE['do4me_remember']);                    setcookie('do4me_username',null, -1, '/');                    setcookie('do4me_password',null, -1, '/');                    setcookie('do4me_remember',null, -1, '/');                }            echo "yesadmin";        }else{            $query = "select * from `d4musers` where `user_email` = '".$name."' and `user_pwd` = '".$password."'";            $result=mysqli_query($this->conn,$query);            $value=mysqli_fetch_assoc($result);            if(isset($value['id']) && $value['id']!=0){                $_SESSION['d4mlogin_user_id'] = $value['id'];                $_SESSION['d4museremail'] = $value['user_email'];                    if($t